import { useReducer } from 'react';

import CartContext from './CartContext';

const defaultCartState = {
  items: [],
  totalAmount: 0
};

const cartReducer = (state, action) => {
  if (action.type === 'ADD') {
    const updatedTotalAmount = state.totalAmount+ action.item.price * action.item.amount;
    const existingCartItemIndex = state.items.findIndex(item=>item.id===action.item.id) //to check if item is part of cart findIndex which finds index of item in an arrray.
// returns index of the item if it exists.
    const existingCartItem = state.items[existingCartItemIndex] // get the item based on index else existingCartItem is null

    let updatedItem;
    let updatedItems;

    if(existingCartItem){
      const updatedItem={
        ...existingCartItem,
        amount : existingCartItem.amount +action.item.amount
      }
      updatedItems =[...state.items]
      updatedItems[existingCartItemIndex]=updatedItem // overriding existing cart item with new updated items
    } else{
    
      updatedItems = state.items.concat(action.item);
    }

    //const updatedItems = state.items.concat(action.item);  // returns a new array. concat.
    //const updatedTotalAmount = state.totalAmount + action.item.price * action.item.amount;
    return {
      items: updatedItems,
      totalAmount: updatedTotalAmount
    };
  }

  if(action.type==='REMOVE'){
    const existingCartItemIndex = state.items.findIndex(item=>item.id===action.id) 
    const existingItem = state.items[existingCartItemIndex]
    const updatedTotalAmount = state.totalAmount-existingItem.price
    let updatedItems;
    if(existingItem.amount ===1){
      updatedItems= state.items.filter(item => item.id !== action.id)
    }else{
      const updatedItem = {...existingItem,amount: existingItem.amount-1}
      updatedItems =[...state.items]
      updatedItems[existingCartItemIndex]=updatedItem
    }

  

  return{
    items:updatedItems,
    totalAmount : updatedTotalAmount

  }
}
   return defaultCartState
   
};

const CartProvider = (props) => {
  const [cartState, dispatchCartAction] = useReducer(cartReducer, defaultCartState); 
  const addItemToCartHandler = (item) => {
    dispatchCartAction({type: 'ADD', item: item});
  };

  const removeItemFromCartHandler = (id) => {
    dispatchCartAction({type: 'REMOVE', id: id});
  };

  const cartContext = {
    items: cartState.items,
    totalAmount: cartState.totalAmount,
    addItem: addItemToCartHandler,
    removeItem: removeItemFromCartHandler,
  };

  return (
    <CartContext.Provider value={cartContext}>
      {props.children}
    </CartContext.Provider>
  );
};

export default CartProvider;
